import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([
    { text: "Hello! Ask me about weather in any city!", sender: "bot" }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { text: input, sender: "user" }]);
    setInput("");
    setLoading(true);

    try {
      // Send to backend
      const response = await axios.post("http://localhost:8001/chat", {
        text: input
      });

      // Add bot response
      setMessages(prev => [...prev, { 
        text: response.data.response, 
        sender: "bot" 
      }]);
    } catch (error) {
      console.error("Error:", error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I cannot connect to the server. Make sure backend is running on port 8000.", 
        sender: "bot" 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage();
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>üå§Ô∏è Weather Assistant</h1>
        <p>Ask about weather in any city</p>
      </header>

      <div className="chat-container">
        <div className="messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.sender}-message`}>
              {msg.text}
            </div>
          ))}
          {loading && <div className="loading">Checking weather...</div>}
        </div>

        <form onSubmit={handleSubmit} className="input-container">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your question..."
            className="input-field"
            disabled={loading}
          />
          <button
            type="submit"
            className="send-button"
            disabled={loading || !input.trim()}
          >
            Send
          </button>
        </form>
      </div>

      <div style={{ marginTop: "20px", color: "#666", fontSize: "14px" }}>
        <p>Try asking: "What is the weather in Pune?" or "Weather in Mumbai"</p>
      </div>
    </div>
  );
}

export default App;
