import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { 
      text: "Hello! I can provide real-time weather for ANY city worldwide. Try asking about your city!", 
      sender: 'bot',
      timestamp: new Date().toLocaleTimeString()
    }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (text) => {
    if (!text.trim()) return;

    const userMessage = {
      text: text,
      sender: 'user',
      timestamp: new Date().toLocaleTimeString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:8001/chat', {
        text: text
      });

      const botMessage = {
        text: response.data.response,
        sender: 'bot',
        timestamp: new Date().toLocaleTimeString()
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      
      const errorMessage = {
        text: "Sorry, I'm having trouble connecting. Please check if the backend is running on port 8001.",
        sender: 'bot',
        timestamp: new Date().toLocaleTimeString()
      };
      
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage(input);
  };

  const exampleQueries = [
    "What's the weather in Tokyo?",
    "How hot is Dubai right now?",
    "Weather in Paris, France",
    "Temperature in Sydney, Australia",
    "Is it raining in Seattle?"
  ];

  return (
    <div className="App">
      <header className="app-header">
        <h1>üåç Global Weather Assistant</h1>
        <p>Real-time weather for any city worldwide using AI</p>
        <div className="api-status">
          <span className="status-dot"></span>
          <small>Connected to real weather API</small>
        </div>
      </header>

      <div className="chat-container">
        <div className="messages-container">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`message ${message.sender}-message`}
            >
              <div className="message-content">
                {message.text.split('\n').map((line, i) => (
                  <p key={i}>{line}</p>
                ))}
              </div>
              <div className="timestamp">{message.timestamp}</div>
            </div>
          ))}
          {loading && (
            <div className="loading">
              <div className="spinner"></div>
              <span>Getting real-time weather data...</span>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form onSubmit={handleSubmit} className="input-container">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about weather in any city (e.g., 'Weather in Berlin')"
            className="input-field"
            disabled={loading}
          />
          <button
            type="submit"
            className="send-button"
            disabled={loading || !input.trim()}
          >
            {loading ? '...' : 'Send'}
          </button>
        </form>
      </div>

      <div className="examples">
        <h3>Try asking about:</h3>
        <div className="example-queries">
          {exampleQueries.map((query, index) => (
            <button
              key={index}
              className="example-query"
              onClick={() => sendMessage(query)}
              disabled={loading}
            >
              {query}
            </button>
          ))}
        </div>
      </div>

      <footer className="app-footer">
        <p>Powered by: WeatherAPI.com + OpenRouter AI + LangChain</p>
      </footer>
    </div>
  );
}

export default App;
