import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { text: "Ask about weather in any city", sender: 'bot' }
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { text: input, sender: 'user' }]);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:8001/chat', {
        text: input
      });

      // Add bot response
      setMessages(prev => [...prev, { 
        text: response.data.response, 
        sender: 'bot' 
      }]);
    } catch (error) {
      setMessages(prev => [...prev, { 
        text: "Connection error. Check if backend is running on port 8001.", 
        sender: 'bot' 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage();
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>üå§Ô∏è Weather Assistant</h1>
      
      <div style={styles.chatBox}>
        {messages.map((msg, index) => (
          <div 
            key={index} 
            style={{
              ...styles.message,
              ...(msg.sender === 'user' ? styles.userMessage : styles.botMessage)
            }}
          >
            {msg.text}
          </div>
        ))}
        {loading && <div style={styles.loading}>Checking weather...</div>}
      </div>

      <form onSubmit={handleSubmit} style={styles.inputForm}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask weather for any city..."
          style={styles.input}
          disabled={loading}
        />
        <button 
          type="submit" 
          style={styles.button}
          disabled={loading || !input.trim()}
        >
          Send
        </button>
      </form>

      <div style={styles.examples}>
        <small>Try: "Weather in Tokyo" or "How hot is Dubai?"</small>
      </div>
    </div>
  );
}

// Minimal inline styles
const styles = {
  container: {
    maxWidth: '600px',
    margin: '40px auto',
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  },
  header: {
    textAlign: 'center',
    color: '#333',
    marginBottom: '30px'
  },
  chatBox: {
    background: '#f9f9f9',
    borderRadius: '10px',
    padding: '20px',
    minHeight: '400px',
    marginBottom: '20px',
    overflowY: 'auto'
  },
  message: {
    padding: '12px',
    margin: '8px 0',
    borderRadius: '8px',
    maxWidth: '80%',
    wordWrap: 'break-word'
  },
  userMessage: {
    background: '#007bff',
    color: 'white',
    marginLeft: 'auto',
    textAlign: 'right'
  },
  botMessage: {
    background: '#e9ecef',
    color: '#333',
    marginRight: 'auto'
  },
  loading: {
    textAlign: 'center',
    color: '#666',
    padding: '10px'
  },
  inputForm: {
    display: 'flex',
    gap: '10px'
  },
  input: {
    flex: 1,
    padding: '12px',
    border: '1px solid #ddd',
    borderRadius: '6px',
    fontSize: '16px'
  },
  button: {
    padding: '12px 24px',
    background: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    fontSize: '16px'
  },
  examples: {
    textAlign: 'center',
    color: '#666',
    marginTop: '20px',
    fontSize: '14px'
  }
};

export default App;
